import React from "react";

import {SpinnerOverlay,SpinnerContainer} from "./with-spinner.styles.jsx";

const WithSpinner = wrappedComponent => {
  const spinner = ({isLoading, ...otherProps}) => {
  return isLoading ? (
    <SpinnerOverlay>
      <SpinnerContainer />
     </SpinnerOverlay>
  ) : (
    <wrappedComponent />
  )

};
return spinner;
};

export default WithSpinner;
