import React,{useEffect,lazy,Suspense} from 'react';
import {Switch,Route,Redirect} from "react-router-dom";
import {createStructuredSelector} from "reselect";

import { connect } from "react-redux";
import ErrorBoundary from "./components/error-boundary/error-boundary.component";
import Header from "./components/header/header.components"
import Spinner from "./components/spinner/spinner.component"
import {selectCurrentUser} from "./redux/user/user.selector";
import {checkUserSession} from "./redux/user/user.actions";
import {  GlobalStyle }  from "./global.styles";

const HomePage = lazy(() => import('./pages/homepage/homepage.components'));
const ShopPage = lazy(() => import('./pages/shop/shop.components'));
const SignINAndSignUp = lazy(() => import('./pages/sign-in-and-sign-up/sign-in-and-sign-up.components'));
const CheckoutPage = lazy(() => import('./pages/checkout/checkout.components'));

const App = ({currentUser,checkUserSession}) => {
    useEffect(() => {
       checkUserSession();
    },[checkUserSession]);

   return (
     <div>
        <GlobalStyle />
       <Header />
       <Switch>
       <ErrorBoundary>
          <Suspense fallback = {  <Spinner /> }>
           <Route exact path='/' component={HomePage} />
         <Route path='/shop' component={ShopPage} />
         <Route exact path='/checkout' component={CheckoutPage} />
         <Route
           exact
           path='/signin'
           render={() =>
             currentUser ? (
               <Redirect to='/' />
             ) : (
               <SignINAndSignUp />
             )
           }
         />
         </Suspense>
         </ErrorBoundary>
       </Switch>
     </div>
   );
 }


const mapStateToProps = createStructuredSelector ({
  currentUser: selectCurrentUser
});

const mapDispatchToProps = dispatch => ({
  checkUserSession: () => dispatch(checkUserSession())
});



export default connect(mapStateToProps,mapDispatchToProps)(App);
