import React from "react";

import stripeCheckout from "react-stripe-cheeckout";

const stripeCheckoutButton = ({price}) => {
  const priceForStripe = price*100;
  const publishablekey = 'pk_test_51HD4EoIwAVryXs1m20uDSxFq6l61GNrYKQeJJtxE9t8X5GGBqCmk5WXIN9aqRQjOZC6M1JpZvgBD13Tu1dhSkrIF00i1kVop36'

const  onToken = token => {
    console.log(token);
    alert('Payment Successful');
  }


  return (
    <StripeCheckout
      label = 'Pay Now'
      name = 'CRWN Clothing Ltd.'
      billingAddress
      shippingAddress
      image='https://svgshare.com/i/Cuz.svg'
      description = {`Your total is $${price}`}
      amount ={priceForStripe}
      panelLabel = "Pay Now"
      token ={onToken}
      stripeKey = {publishablekey}
    />
  )
};

export default stripeCheckoutButton;
